mapper.insert:
INSERT INTO BOARD (
	  ID
	, SUBJECT
	, CONTENT
	, EMAIL
	, VIEW_CNT
	, CRT_DT
	, MDFY_DT
	, FILE_NAME
	, ORIGIN_FILE_NAME
	, IP_ADDR)
	VALUES (
	  'BD-' || TO_CHAR(SYSDATE, 'YYYYMMDD') || '-' || LPAD(SEQ_TABLE_PK.NEXTVAL, 6, '0')
	, #{subject}
	, #{content}
	, #{email}
	, 0
	, SYSDATE
	, NULL
	, #{fileName}
	, #{originFileName}
	, #{ipAddr}
	)

mapper.update:
UPDATE BOARD
   SET VIEW_CNT = VIEW_CNT + 1
 WHERE ID = #{id}

UPDATE BOARD
   SET SUBJECT = #{subject}
     , CONTENT = #{content}
     , EMAIL = #{email}
     , MDFY_DT = SYSDATE

     , FILE_NAME = #{fileName}
     , ORIGIN_FILE_NAME = #{originFileName}

 WHERE ID = #{id}
